mapper.select:
SELECT id AS device_id , motor_id , member_id , product_id , model_name , serial_number , status , notes , TO_CHAR(insert_date, 'YYYY-MM-DD') AS insert_date FROM public.ssp_device
AND product_id = #{product_id}
AND status = #{status}
ORDER BY serial_number

SELECT
	COUNT(*)
FROM
	public.ssp_device

	product_id = #{product_id}

		AND status = #{status}

mapper.update:
UPDATE
	public.ssp_device
SET 
	  status = #{status}
	, notes = #{notes} 
	, update_member_id = #{login_id}::bigint
	, update_date = now()
WHERE
	id = #{ssp_device_id}
