mapper.select:
SELECT m.id as menu_id , m.step , m.parent_id , m.name , m.sort , m.url , m.link_yn , m.default_utype , m.default_yn , m.use_yn , m.limit_ip_yn FROM member.menu m
AND default_utype = #{default_utype}
ORDER BY m.step, m.sort

mapper.insert:
INSERT INTO member.menu(
    step
    , parent_id
    , name
    , use_yn
    , link_yn
    , url
    , sort
    , default_utype
    , default_yn
    , insert_date
)VALUES(
    #{step}
    , #{parent_id}
    , #{name}
    , #{use_yn}
    , #{link_yn}
    , #{url}
    , #{sort}
    , #{default_utype}
    , #{default_yn}
    , now()
);

mapper.update:
UPDATE
	member.menu
SET
	parent_id = #{parent_id}
	, step = #{step}
	, url = #{url}
	, link_yn = #{link_yn}
	, use_yn = #{use_yn}
	, sort = #{sort}
WHERE
	id = #{menu_id}
